import styles from "./styles/App.module.css";
import { useState, useEffect } from "react";
let eleccionJugador = null;
let eleccionComputadora = null;
function App() {
  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  let [resultado, setresultado] = useState(
    "al presionar uno de los botones la computadora elegira alguna de las tres opciones  y te mostrara el resultado"
  );
  let [parrafoComputadora, setparrafoComputadora] = useState("");

  function imprimirEleccionComputadora() {
    if (eleccionComputadora == 1) {
      setparrafoComputadora("la computadora eligio piedra");
    } else if (eleccionComputadora == 2) {
      setparrafoComputadora("la computadora eligio papel");
    } else if (eleccionComputadora == 3) {
      setparrafoComputadora("la computadora eligio tijera");
    }
  }

  function generarResultado() {
    //eleccion computadora es piedra=1 papel= 2 tijera =3
    if (eleccionComputadora == eleccionJugador) {
      setresultado("empate");
    }
    if (eleccionComputadora == 2 && eleccionJugador == 1) {
      setresultado("perdiste");
    }
    if (eleccionComputadora == 3 && eleccionJugador == 1) {
      setresultado("ganaste");
    }
    if (eleccionJugador == 2 && eleccionComputadora == 3) {
      setresultado("perdiste");
    }
    if (eleccionJugador == 3 && eleccionComputadora == 2) {
      setresultado("ganaste");
    }
    if (eleccionComputadora == 1 && eleccionJugador == 2) {
      setresultado("ganaste");
    }
    if (eleccionComputadora == 1 && eleccionJugador == 3) {
      setresultado("perdiste");
    }
  }

  return (
    <>
      {" "}
      <h1 className={styles.parrafos}>Juego pierda, palel o tijeras</h1>
      <div className={styles.contenedorBotones}>
        <div className={styles.boton}>
          <button
            onClick={() => {
              eleccionJugador = 1;
              eleccionComputadora = getRandomInt(1, 3);
              imprimirEleccionComputadora();
              generarResultado();
            }}
          >
            <img
              src="data:image/jpeg;base64,"
              alt=""
            />{" "}
            piedra
          </button>
        </div>
        <button
          onClick={() => {
            eleccionJugador = 2;
            eleccionComputadora = getRandomInt(1, 3);
            imprimirEleccionComputadora();
            generarResultado();
          }}
        >
          <img
            src="data:image/png;base64,"
            alt=""
          />{" "}
          papel{" "}
        </button>
        <button
          onClick={() => {
            eleccionJugador = 3;
            eleccionComputadora = getRandomInt(1, 3);
            imprimirEleccionComputadora();
            generarResultado();
          }}
        >
          <img
            src="data:image/jpeg;base64,"
            alt=""
          />{" "}
          tijera{" "}
        </button>
      </div>
      <div className={styles.parrafos}>
        <p className={() => {}}>{parrafoComputadora}</p>
        <p className={() => {}}>{resultado}</p>
      </div>
    </>
  );
}

export default App;
